const productBuyBtn=document.querySelector(".catalog-product-card__buy-btn"),modalWindow=document.querySelector(".modal-in-cart"),modalCloseBtns=modalWindow.querySelector(".modal-close-btn");productBuyBtn.addEventListener("click",()=>{modalWindow.classList.add("modal--active")}),modalCloseBtns.addEventListener("click",()=>{modalWindow.classList.remove("modal--active")}),document.addEventListener("keydown",o=>{!modalWindow.classList.contains("modal--active")||"Esc"!==o.key&&"Escape"!==o.key||modalWindow.classList.remove("modal--active")});
